let query ={};
query.startkey = msg.startkey;
query.endkey = msg.endkey;
msg.payload = query;
return msg;

var label = msg.day;
var data = []

var target = msg.target? msg.target.value.value:flow.get('target')[msg.event];


//initialize 0 got all days
for(let day of label){
    data.push(0);
}

//assign value
for(let payload of msg.payload){
    if(label.includes(payload.key)){
        data[label.indexOf(payload.key)] = Math.round((data[label.indexOf(payload.key)] + parseFloat(payload.value)) * 100)/100;
    }

}

//check negative value
let max = data[0];
let min = data[0];
for(let data1 of data){
    if(data1 > max){
        max = data1;
    }
    if(data1 < min){
        min = data1;
    }
    if(data1 < 0){
        data1 = 0;
    }
}

let trendUp = true;
let ofTarget = Math.round(((data[6]-target)/target)*100 *100)/100
if(ofTarget<0){
    trendUp = false;
}

let payload = {
    trendUp: trendUp,
    value: data[6],
    ofTarget: ofTarget,
    setpoint: target,
    data: data,
    labels: label,
    min:min,
    max:max
}
msg.socketIOEvent = msg.event
msg.payload = payload
flow.set(msg.event+"Temp", payload)
flow.set(msg.event+"NewData", false);
return msg;

