{
  "_id": "_design/device",
  "views": {
    "device": {
      "map": "function (doc) {
  if(doc.ID)
  emit([doc.location, doc.ID], doc);
}",
      "reduce": "function (keys, values, rereduce) {
  var max = values[0];
       for(var i=0; i<values.length; i++){
          if(values[i].ts > max.ts){
            max = values[i]
          }
        }
        return max;
}"
    }
  },
  "language": "javascript"
}

{
  "_id": "_design/stats",
  "views": {
    "stats": {
      "reduce": "function (keys, values, rereduce) {
  if (rereduce) {
        return {
          "pres":{
            'min': values.reduce(function(a, b) { return Math.min(a, b.pres.min) }, Infinity),
            'max': values.reduce(function(a, b) { return Math.max(a, b.pres.max) }, -Infinity)
          },
          "gas":{
            'min': values.reduce(function(a, b) { return Math.min(a, b.gas.min) }, Infinity),
            'max': values.reduce(function(a, b) { return Math.max(a, b.gas.max) }, -Infinity)
          },
          "IAQ":{
            'min': values.reduce(function(a, b) { return Math.min(a, b.IAQ.min) }, Infinity),
            'max': values.reduce(function(a, b) { return Math.max(a, b.IAQ.max) }, -Infinity)
          },
          "temp":{
            'min': values.reduce(function(a, b) { return Math.min(a, b.temp.min) }, Infinity),
            'max': values.reduce(function(a, b) { return Math.max(a, b.temp.max) }, -Infinity)
          },
          "hum":{
            'min': values.reduce(function(a, b) { return Math.min(a, b.hum.min) }, Infinity),
            'max': values.reduce(function(a, b) { return Math.max(a, b.hum.max) }, -Infinity)
          },
          "eqco2":{
            'min': values.reduce(function(a, b) { return Math.min(a, b.eqco2.min) }, Infinity),
            'max': values.reduce(function(a, b) { return Math.max(a, b.eqco2.max) }, -Infinity)
          },
          "VOC":{
            'min': values.reduce(function(a, b) { return Math.min(a, b.VOC.min) }, Infinity),
            'max': values.reduce(function(a, b) { return Math.max(a, b.VOC.max) }, -Infinity)
          },
          "iaqA":{
            'min': values.reduce(function(a, b) { return Math.min(a, b.iaqA.min) }, Infinity),
            'max': values.reduce(function(a, b) { return Math.max(a, b.iaqA.max) }, -Infinity)
          }
        }
    } else {
       var pres = []
       var gas = []
       var IAQ = []
       var iaqA = []
       var temp = []
       var hum = []
       var eqco2 = []
       var VOC = []
        values.forEach(function(value){
          pres.push(value.pres)
          gas.push(value.gas)
          IAQ.push(value.IAQ)
          iaqA.push(value.iaqA)
          temp.push(value.temp)
          hum.push(value.hum)
          eqco2.push(value.eqco2)
          VOC.push(value.VOC)
        })
        return {
          "pres":{
            'min': Math.min.apply(null, pres),
            'max': Math.max.apply(null, pres)
          },
          "gas":{
            'min': Math.min.apply(null, gas),
            'max': Math.max.apply(null, gas)
          },
          "IAQ":{
            'min': Math.min.apply(null, IAQ),
            'max': Math.max.apply(null, IAQ)
          },
          "temp":{
            'min': Math.min.apply(null, temp),
            'max': Math.max.apply(null, temp)
          },
          "hum":{
            'min': Math.min.apply(null, hum),
            'max': Math.max.apply(null, hum)
          },
          "eqco2":{
            'min': Math.min.apply(null, eqco2),
            'max': Math.max.apply(null, eqco2)
          },
          "VOC":{
            'min': Math.min.apply(null, VOC),
            'max': Math.max.apply(null, VOC)
          },
          "iaqA":{
            'min': Math.min.apply(null, iaqA),
            'max': Math.max.apply(null, iaqA)
          }


        }
    }
}",
      "map": "function (doc) {
  if(doc.ID)
  emit(doc.ID, doc);
}"
    }
  },
  "language": "javascript"
}

