const preChamber = msg.preChamber;
const chamber = msg.chamber;
const chamberId = msg.original.pupationChamberId;
const rackId = msg.original.pupationRackID;
const rack = msg.rack;
var exceedCapacity = false;
var rackOccupied = false;
var chamberOccupied = false;
var availableNumber;
var notValid = false;
var inAnotherPupationChamber = false;
var inPupationChamber;
if(chamber.options[chamberId].status == "OCCUPIED"){
    chamberOccupied = true;
}
else{
    chamberOccupied = false;

    // if(!chamber.options[chamberId].batchIdList){
    //     chamber.options[chamberId].batchIdList =[]
    // }
    // for(let batchId of msg.original.batchId){
    //     if(!chamber.options[chamberId].batchIdList.includes(batchId)){
    //         chamber.options[chamberId].batchIdList.push(batchId)
    //     }
    // }
    if(!chamber.options[chamberId].rackIdList){
        chamber.options[chamberId].rackIdList =[]
    }
    if(!chamber.options[chamberId].rackIdList.includes(rackId)){
            chamber.options[chamberId].rackIdList.push(rackId)
            chamber.options[chamberId].occupiedCapacity= chamber.options[chamberId].occupiedCapacity +1;
    }
    if(chamber.options[chamberId].occupiedCapacity == chamber.options[chamberId].capacity){
        chamber.options[chamberId].status = "OCCUPIED"
    }
    else{
        chamber.options[chamberId].status = "PARTIALLY_OCCUPIED"
    }
}


//from prePupaeChamber to pupae, assuming only one setupId per rack
if(rack.options[rackId].prePupaeChamberId){
    node.warn("pre to pupa process")

    rack.options[rackId].pupationChamberId = chamberId;
    var preChamberId = rack.options[rackId].prePupaeChamberId
    preChamber.options[preChamberId].occupiedCapacity = preChamber.options[preChamberId].occupiedCapacity -1;
    preChamber.options[preChamberId].rackIdList.splice(preChamber.options[preChamberId].rackIdList.indexOf(rackId));
    if(preChamber.options[preChamberId].occupiedCapacity == 0){
        preChamber.options[preChamberId].status = "AVAILABLE"
    }
    else{
        preChamber.options[preChamberId].status = "PARTIALLY_OCCUPIED"
    }

        // if(!rack.options[rackId].batchIdList){
        //   rack.options[rackId].batchIdList =[]
        // }

         if(!rack.options[rackId].setupDateId){
          rack.options[rackId].setupDateId =[]
        }

        //  if(!rack.options[rackId].setupDateId){
        //   rack.options[rackId].setupDateId = {}
        // }

         let found = false;
            for(let setupDateIdDB of rack.options[rackId].setupDateId) {
                if(setupDateIdDB == msg.setupDateId){
                    found = true;
                }
            }
            if(!found) {
                rack.options[rackId].setupDateId.push(msg.setupDateId)
            }

        // rack.options[rackId].setupDateId[msg.setupDateId] = msg.setupDateId

        // Sprint 14 - No more batchId field, but kept it for reference
        // if (msg.original.batchId) for(let batchId of msg.original.batchId){
        //     if(!rack.options[rackId].batchIdList.includes(batchId)){
        //         rack.options[rackId].batchIdList.push(batchId)
        //     }
        // }

    rack.options[rackId].occupiedCapacity = parseFloat(msg.original.trayCount);
    if(rack.options[rackId].occupiedCapacity > rack.options[rackId].capacity){
        exceedCapacity = true
        availableNumber = rack.options[rackId].capacity;
    }
    else if(rack.options[rackId].occupiedCapacity == rack.options[rackId].capacity){
        rack.options[rackId].status = "OCCUPIED"
    }
    else{
        rack.options[rackId].status = "PARTIALLY_OCCUPIED"
    }    
    delete rack.options[rackId].prePupaeChamberId;
}
else if(rack.options[rackId].pupationChamberId){
    //add tray to this rack (PUPA chamber)
    node.warn("add tray to pupa")

    if(rack.options[rackId].status == "OCCUPIED"){
        rackOccupied = true;
    }
    else{
        rackOccupied = false;
        if(rack.options[rackId].pupationChamberId != chamberId){
            inAnotherPupationChamber = true;
            inPupationChamber = rack.options[rackId].pupationChamberId
        }
        if(!rack.options[rackId].batchIdList){
           rack.options[rackId].batchIdList =[]
        }

         if(!rack.options[rackId].setupDateId){
           rack.options[rackId].setupDateId =[]
        }

        // Sprint 14 - No more batchId field, but kept it for reference
        // if (msg.original.batchId) for(let batchId of msg.original.batchId){
        //     if(!rack.options[rackId].batchIdList.includes(batchId)){
        //         rack.options[rackId].batchIdList.push(batchId)
        //     }
        // }
        let found = false;
            for(let setupDateIdDB of rack.options[rackId].setupDateId) {
                if(setupDateIdDB == msg.setupDateId){
                    found = true;
                }
            }
            if(!found) {
                rack.options[rackId].setupDateId.push(msg.setupDateId)
            }


        rack.options[rackId].occupiedCapacity = rack.options[rackId].occupiedCapacity +parseFloat(msg.original.trayCount);
        if(rack.options[rackId].occupiedCapacity > rack.options[rackId].capacity){
            exceedCapacity = true
            availableNumber = rack.options[rackId].capacity - (rack.options[rackId].occupiedCapacity - msg.original.trayCount)
        }
        else if(rack.options[rackId].occupiedCapacity == rack.options[rackId].capacity){
            rack.options[rackId].status = "OCCUPIED"
        }
        else{
            rack.options[rackId].status = "PARTIALLY_OCCUPIED"
        }


    }

}
else{
    //not from prepupae chamber and not in pupation chamber
    notValid = true;
}




    // rack.options[rackId].occupiedCapacity = rack.options[rackId].occupiedCapacity +parseFloat(msg.original.trayCount);
    // if(rack.options[rackId].occupiedCapacity > rack.options[rackId].capacity){
    //     exceedCapacity = true
    //     availableNumber = rack.options[rackId].capacity - (rack.options[rackId].occupiedCapacity - msg.original.trayCount)
    // }
    // else if(rack.options[rackId].occupiedCapacity == rack.options[rackId].capacity){
    //     rack.options[rackId].status = "OCCUPIED"
    // }
    // else{
    //     rack.options[rackId].status = "PARTIALLY_OCCUPIED"
    // }




msg.error = false;
if(notValid){
    msg.error = true;
    msg.errMsg = "The rack is not from PrePupae Chamber and not in Pupation Chamber."
}
else if(inAnotherPupationChamber){
    msg.error = true;
    msg.errMsg = "The rack is in another Pupation Chamber. Do you mean: " + inPupationChamber;
}
else if(exceedCapacity){
    msg.error = true;
    msg.errMsg = "Not enough capacity, capacity available: " + availableNumber;
}
else if(rackOccupied && chamberOccupied){
    msg.error = true;
    msg.errMsg = "Rack and Chamber are occupied";
}
else if(rackOccupied){
    msg.error = true;
    msg.errMsg = "Rack is occupied";
}
else if(chamberOccupied){
    msg.error = true;
    msg.errMsg = "Chamber is occupied";
}
msg.rack = rack;
msg.chamber = chamber;
msg.preChamber = preChamber;
msg.payload = ["rack", "chamber", "preChamber"]

return msg;

