const setpoint = 30;
const maxScore = 100;
const minScore = 0;

var temp4Arr = flow.get("temp4") || [];
var temp200Arr = flow.get("temp200") || [];
var currentTemp = msg.payload.temp;

var different = Math.abs(currentTemp - setpoint);
var score = 0;
if(different<=1){
    score = maxScore - minScore;
}
else if(different<=5){
    score = 0.5*(maxScore - minScore);
}
else if(different<=10){
    score = 0.2*(maxScore - minScore);
}
else{
    score = 0;
}

temp4Arr.push(score);
temp200Arr.push(score);
if(temp4Arr.length>4){
    temp4Arr.shift();
}
if(temp200Arr.length>200){
    temp200Arr.shift();
}
