var avg200 = temp200Arr.reduce((a,b)=>(a+b)) / temp200Arr.length;

scoreRatio = ((avg4 - avg200)/avg200)*100;


msg.payload = {}
msg.payload.value = parseFloat(currentTemp.toFixed(1));
msg.payload.differentValue = parseFloat(scoreRatio.toFixed(2));


return msg;

