function getTemplate(productionLine, workstationForm) {
    let docs = msg.templateDocs
    let compareProdLine = false

    for (let doc of docs) {
        let unmatchProdLine = doc.productionLine !== productionLine
        let unmatchWorkForm = doc.workstationForm !== workstationForm

        if (compareProdLine && unmatchProdLine) continue
        if (unmatchWorkForm) continue

        return doc.template
    }

    return [
        {label: 'Name', value: 'user_name'},
        {label: 'Submission On', value: 'submission_timestamp'},
        {label: 'Reported On', value: 'timestamp'},
        {label: 'Form Version', value: 'form_version'}
    ]
}

function group2(sop, submissions) {
    let grouped = []

    for (let submission of submissions) {
        let submissionForm = submission.workstationForm
        let sopKey = sop.key
        let match = submissionForm === sopKey

        if (match) grouped.push(submission)
    }

    return grouped
}

function group1(sopType, submissions) {
    let grouped = []

    // node.warn(submissions)

    for (let submission of submissions) {
        let submissionLine = submission.productionLine
        let sopTypeKey = sopType.key
        let match = submissionLine === sopTypeKey

        if (match) grouped.push(submission)
    }

    return grouped
}

