  "views": {
    "unoccupied-by-sublocation": {
      "map": "function (doc) {
  if (doc._id === "asset") {
    for (var assetName in doc.assets) {
      var asset = doc.assets[assetName];
      var assetCapacity = asset.capacity || 0;
      var assetSetupDateIds = asset.setupDateIds || {};
      var assetSublocation = asset.sublocation || "";
      var value = assetName;
      var label = asset.label;
      for (var setupDateId in assetSetupDateIds) {
        var setupDateIdOccupied = assetSetupDateIds[setupDateId] || 0;
        assetCapacity = assetCapacity - setupDateIdOccupied;
      }
      if (assetCapacity > 0) emit([assetSublocation], {value: value, label: label});
    }
  }
}"
    }
  },
  "language": "javascript"
}

{
  "_id": "_design/sublocations",
  "_rev": "18-11f41258930ba3fecd0269d41e3829ee",
  "views": {
    "by-asset": {
      "map": "function (doc) {
  if (doc._id === "asset") {
    for (var assetName in doc.assets) {
      var asset = doc.assets[assetName];
      var assetSublocation = asset.sublocation;

      if (!assetSublocation) return;
      var value = assetSublocation;
      var label = doc.sublocations[assetSublocation].label;
      emit([assetName], {value: value, label: label});
    }
  }
}"
    },
    "unoccupied-by-location": {
      "map": "function (doc) {
  if (doc._id === "asset") {
    for (var sublocationName in doc.sublocations) {
      var sublocation = doc.sublocations[sublocationName];
      var sublocationLocation = sublocation["location"];
      var capacity = sublocation.capacity || 0;
      var value = sublocationName;
      var label = sublocation.label;

      for (var assetName in doc.assets) {
        var asset = doc.assets[assetName];
        var assetSublocation = asset["sublocation"];

        if (assetSublocation === value && capacity) capacity--;
      }

      if (capacity > 0) emit([sublocationLocation], {value: value, label: label});
    }
  }
}"
    }
  },
