function convertNest(deviceData, redisValue) {
    const time = transformTime(deviceData.ts)
    for (const loopKey in deviceData) {
        const loopValue = deviceData[loopKey]
        if (loopKey != 'values') redisValue[loopKey] = loopValue
    }
    if (!redisValue.values) redisValue.values = {}
    if (!redisValue.values[time]) redisValue.values[time] = {}
    redisValue.values[time] = deviceData.values
    return redisValue
}

function convertFlat(deviceData, redisValue) {
    const time = transformTime(deviceData.ts)
    for (const loopKey in deviceData) {
        const loopValue = deviceData[loopKey]
        if (isMetadata(loopKey)) {
            redisValue[loopKey] = loopValue
        } else {
            if (!redisValue.values) redisValue.values = {}
            if (!redisValue.values[time]) redisValue.values[time] = {}
            redisValue.values[time][loopKey] = loopValue
        }
    }
    return redisValue
}

function isMetadata(key) {
    const metadata = [
        'ID', 
        'ts', 
        'uptime', 
        'location', 
        'deviceName',
        'recipeVer',
        'IP',
        'firmwareVer'
    ]

    return metadata.includes(key)
}

function transformTime(unix) {
    let d = new Date(unix * 1000)
    let hours = d.getUTCHours()
    let minutes = d.getUTCMinutes()
    let seconds = d.getUTCSeconds()

    // Round down
    if (seconds <= 10) seconds = 0
    else if (seconds <= 20) seconds = 10
    else if (seconds <= 30) seconds = 20
    else if (seconds <= 40) seconds = 30
    else if (seconds <= 50) seconds = 40
    else seconds = 50

    let transformed = ''
    transformed += formatDateDigits(hours)
    transformed += formatDateDigits(minutes)
    transformed += formatDateDigits(seconds)

    return transformed
}

function formatDateDigits(digits) {
    let str = digits.toString()
    if (str.length > 1) return str
    else return '0' + str
}

