let dates = []

// Generate days based on filters
let startDate = new Date()
startDate.setDate(startDate.getDate() - 30)
let endDate = new Date()
endDate.setDate(endDate.getDate() - 1)

// For support different timezone
// startDate.setDate(startDate.getDate() - 1)
// endDate.setDate(endDate.getDate() + 1)

// Generate days
for (let d = startDate; d <= endDate; d.setDate(d.getDate() + 1)) {
    let seconds = d.getTime() / 1000
    dates.push(transformDate(seconds))
}

msg.dates = dates
return msg;

function transformTime(unix) {
    let d = new Date(unix * 1000)
    let hours = d.getUTCHours()
    let minutes = d.getUTCMinutes()
    let seconds = d.getUTCSeconds()

    // Round down
    if (seconds <= 10) seconds = 0
    if (seconds <= 20) seconds = 10
    if (seconds <= 30) seconds = 20
    if (seconds <= 40) seconds = 30
    if (seconds <= 50) seconds = 40
    else seconds = 50

    let transformed = ''
    transformed += formatDateDigits(hours) + ':'
    transformed += formatDateDigits(minutes) + ':'
    transformed += formatDateDigits(seconds)

    return transformed
}

function transformDate(unix) {
    let d = new Date(unix * 1000)
    let year = d.getUTCFullYear()
    let month = d.getUTCMonth() + 1
    let date = d.getUTCDate()

    let transformed = ''
    transformed += formatDateDigits(year) + '-'
    transformed += formatDateDigits(month) + '-'
    transformed += formatDateDigits(date)

    return transformed
}

function formatDateDigits(digits) {
    let str = digits.toString()
    if (str.length > 1) return str
    else return '0' + str
}

